$(document).ready(function(){
  $('.owl-carousel').owlCarousel({
      loop:true,
      margin:10,
      responsiveClass:true,
      responsive:{
          0:{
              items:1,
              nav:false,
              dots: false
          },
          600:{
              items:3,
              nav:true
          },
          1000:{
              items:3,
              nav:true,
          }
      }
  })
});
